import { Injectable } from '@angular/core';

interface Contact {
  id: number;
  name: string;
  imageUrl: string;
}

interface Post {
  id: number;
  author: {
    name: string;
    avatarUrl: string;
  };
  timePosted: string;
  content: string;
  imageUrl: string;
}

interface CurrentUser {
  name: string;
  avatarUrl: string;
}

@Injectable({
  providedIn: 'root'
})
export class UserService {
  private contacts: Contact[] = [
    {
      id: 1,
      name: 'Dennis Han',
      imageUrl: 'data:image/jpeg;base64,'
    },
    {
      id: 2,
      name: 'Eric Jones',
      imageUrl: 'https://source.unsplash.com/100x100/?man'
    },
    {
      id: 3,
      name: 'Cynthia Lopez',
      imageUrl: 'https://source.unsplash.com/100x100/?woman'
    },
    {
      id: 4,
      name: 'Anna Becklund',
      imageUrl: 'https://source.unsplash.com/100x100/?girl'
    }
  ];

  private currentUser: CurrentUser = {
    name: 'Current User',
    avatarUrl: 'data:image/jpeg;base64,'
  };

  private posts: Post[] = [
    {
      id: 1,
      author: {
        name: 'Tom Russo',
        avatarUrl: 'https://source.unsplash.com/100x100/?person'
      },
      timePosted: '5 mins',
      content: 'Not having fun at all 😉',
      imageUrl: 'data:image/jpeg;base64,'
    }
  ];

  constructor() { }

  getContacts(): Contact[] {
    return this.contacts;
  }

  getCurrentUser(): CurrentUser {
    return this.currentUser;
  }

  getPosts(): Post[] {
    return this.posts;
  }
}
